@import 'common-variables';
@import 'mixins/text-truncation';
@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;
  width: auto;
  display: flex;
  align-items: center;
  position: relative;

  .tab-items {
    overflow: hidden;
    scroll-behavior: smooth;
    width: 100%;

    .items-content {
      display: flex;
      align-items: center;
    }
  }

  .arrow {
    position: absolute;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 32px;
    height: 32px;
    top: 0;
    bottom: 0;
    left: 0;
    margin: auto 0;
    border-radius: 4px;
    color: var(--theme-btn-invisible-primary--color);
    background-color: var(--theme-btn-invisible-primary--background);
    z-index: 2;

    &:hover {
      color: var(--theme-btn-invisible-primary--color--hover);
      background-color: var(--theme-btn-invisible-primary--background--hover);
    }

    &:active {
      color: var(--theme-btn-invisible-primary--color--active);
      background-color: var(--theme-btn-invisible-primary--background--active);
    }

    &.right {
      left: auto;
      right: 0;
    }
  }

  .overflow-shadow {
    display: block;
    position: relative;
    height: 100%;
    width: 100%;
    pointer-events: all;

    &.shadow-left {
      mask-image: linear-gradient(90deg, transparent 0px, black 45px);
    }

    &.shadow-right {
      mask-image: linear-gradient(
        90deg,
        black calc(100% - 45px),
        transparent 100%
      );
    }

    &.shadow-both {
      mask-image: linear-gradient(
        90deg,
        transparent 0px,
        black 45px,
        black calc(100% - 45px),
        transparent 100%
      );
    }
  }
}
