import { LogicalFilterOperator } from './logical-filter-operator';
export class FilterState {
  public tokens: string[];
  public categories: {
    id: string;
    value: string;
    operator: LogicalFilterOperator;
  }[];
}
/*
 * SPDX-FileCopyrightText: 2023 Siemens AG
 *
 * SPDX-License-Identifier: MIT
 *
 * This source code is licensed under the MIT license found in the
 * LICENSE file in the root directory of this source tree.
 */

export type ToastType = 'info' | 'success' | 'error' | 'warning';
export type ToastPosition = 'bottom-right' | 'top-right';

export interface ToastConfig {
  title?: string;
  message: string | HTMLElement;
  type?: ToastType;
  autoClose?: boolean;
  autoCloseDelay?: number;
  icon?: string;
  iconColor?: string;
}

export function getToastContainer() {
  const containerList = Array.from(
    document.querySelectorAll('ix-toast-container')
  );
  const [container] = containerList;
  if (containerList.length > 1) {
    console.warn(
      'Multiple toast container are found. Only there first is used.'
    );
    return container;
  }
  if (!container) {
    const toastContainer = document.createElement('ix-toast-container');
    document.body.appendChild(toastContainer);

    return toastContainer;
  }
  return container;
}

export function setToastPosition(position: ToastPosition) {
  getToastContainer().position = position;
}

async function toast(config: ToastConfig) {
  const context = getToastContainer();
  const toast = await context.showToast(config);
  return toast;
}

toast.info = (config: ToastConfig) => {
  return toast({
    ...config,
    type: 'info',
  });
};

toast.error = (config: ToastConfig) => {
  return toast({
    ...config,
    type: 'error',
  });
};

toast.success = (config: ToastConfig) => {
  return toast({
    ...config,
    type: 'success',
  });
};

toast.warning = (config: ToastConfig) => {
  return toast({
    ...config,
    type: 'warning',
  });
};

export { toast };
/*
 * SPDX-FileCopyrightText: 2023 Siemens AG
 *
 * SPDX-License-Identifier: MIT
 *
 * This source code is licensed under the MIT license found in the
 * LICENSE file in the root directory of this source tree.
 */

import Animation from '../animation';
import { getCoreDelegate } from '../delegate';

export function showModalLoading(message: string) {
  const modal = document.createElement('ix-modal');
  modal.keyboard = false;

  const loading = document.createElement('ix-modal-loading');
  loading.innerText = message;
  modal.appendChild(loading);

  getCoreDelegate().attachView(modal);
  modal.showModal();

  return {
    update: (text: string) => (loading.innerHTML = text),
    finish: (text?: string, timeout: number = 250) => {
      if (text !== undefined) {
        loading.innerText = text;
      } else {
        timeout = 0;
      }
      setTimeout(() => {
        modal.closeModal(null);
        setTimeout(
          async () => await getCoreDelegate().removeView(modal),
          Animation.mediumTime
        );
      }, timeout);
    },
  };
}
