import { Component, h, Host } from '@stencil/core';

/**
 * @since 1.4.0
 */
@Component({
  tag: 'ix-divider',
  styleUrl: 'divider.scss',
  shadow: true,
})
export class Divider {
  render() {
    return <Host></Host>;
  }
}
