@import 'common-variables';
@import 'mixins/shadow-dom/component';
@import 'legacy/mixins/fonts';
@import 'components/fonts';
@import 'mixins/fonts';

:host {
  @include ix-component;

  display: block;
  position: relative;
}

:host(.text-xs) {
  @include text-xs;
}

:host(.text-s) {
  @include text-s;
}

:host(.text-caption) {
  @include text-caption;
}

:host(.text-caption-single) {
  @include text-caption-single;
}

:host(.text-default) {
  @include text-default;
}

:host(.text-default-single) {
  @include text-default-single;
}

:host(.text-default-title) {
  @include text-default-title;
}

:host(.text-default-title-single) {
  @include text-default-title-single;
}

:host(.text-l) {
  @include text-l;
}

:host(.text-l-single) {
  @include text-l-single;
}

:host(.text-l-title) {
  @include text-l-title;
}

:host(.text-l-title-single) {
  @include text-l-title-single;
}

:host(.text-h2) {
  @include text-h2;
}

:host(.text-xl) {
  @include text-xl;
}

$typography-formats: label, label-xs, label-sm, label-lg, body, body-xs, body-sm,
  body-lg, display, display-xs, display-sm, display-lg, display-xl, display-xxl,
  h6, h5, h4, h3, h2, h1, code, code-sm, code-lg;

@each $format in $typography-formats {
  :host(.typography-#{$format}) {
    @extend .typography-#{$format};
  }
}

$typography-decoration: none, underline, line-through;
@each $decoration in $typography-decoration {
  :host(.typography-decoration-#{$decoration}) {
    @extend .typography-decoration-#{$decoration};
  }
}

:host(.typography-weight-bold) {
  @extend .typography-weight-bold;
}
