import { Component, h, Host } from '@stencil/core';

/**
 * @since 2.0.0
 */
@Component({
  tag: 'ix-modal-content',
  styleUrl: 'modal-content.scss',
  shadow: true,
})
export class ModalContent {
  render() {
    return (
      <Host>
        <slot></slot>
      </Host>
    );
  }
}
