@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;

  display: flex;
  position: relative;
  flex-direction: column;
  align-items: flex-start;
  gap: 0.5rem;
  padding: 1rem;
  height: 100%;
}
import { Component, h, Host } from '@stencil/core';

/**
 * @since 1.6.0
 */
@Component({
  tag: 'ix-card-content',
  styleUrl: 'card-content.scss',
  shadow: true,
})
export class CardContent {
  render() {
    return (
      <Host>
        <slot></slot>
      </Host>
    );
  }
}
