@import 'common-variables';
@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;

  .card {
    background-color: var(--theme-menu--background);
    width: 100%;
    box-shadow: none;
    border: none;
    border-radius: unset;
    display: flex;
    flex-direction: column;

    &.individual {
      box-shadow: 0 0 2px 0 rgba(0, 0, 0, 0.1), 0 4px 8px 0 rgba(0, 0, 0, 0.1),
        0 12px 18px 0px rgba(0, 0, 0, 0.1);
      border: 1px solid var(--theme-menu--border-color);
      border-radius: 4px;
    }

    &.left {
      border-radius: 4px 0 0 4px;
      height: 100%;
    }

    &.right {
      border-radius: 0 4px 4px 0;
      height: 100%;
    }

    .header {
      padding: $default-space;
      flex: 0 1 auto;
    }

    .separator {
      border: 1px solid var(--theme-datepicker-separator--background);
      width: 100%;
      flex: 0 1 auto;
    }

    .content {
      padding: 0px $default-space $default-space $default-space;
      flex: 1 1 auto;
      display: flex;
      flex-direction: column;
      justify-content: center;
    }
  }
}
/*
 * SPDX-FileCopyrightText: 2023 Siemens AG
 *
 * SPDX-License-Identifier: MIT
 *
 * This source code is licensed under the MIT license found in the
 * LICENSE file in the root directory of this source tree.
 */

import { Component, h, Host, Prop } from '@stencil/core';

export type DateTimeCardCorners = 'rounded' | 'left' | 'right';

/**
 * @internal
 */
@Component({
  tag: 'ix-date-time-card',
  styleUrl: 'date-time-card.scss',
  shadow: true,
})
export class DateTimeCard {
  /**
   * set styles
   */
  @Prop() individual: boolean = true;

  /**
   * Set corners style
   */
  @Prop() corners: DateTimeCardCorners = 'rounded';

  private cardClasses() {
    return {
      card: true,
      individual: this.individual,
      left: this.corners === 'left',
      right: this.corners === 'right',
    };
  }

  render() {
    return (
      <Host>
        <div class={this.cardClasses()}>
          <div class="header">
            <slot name="header"></slot>
          </div>

          <div class="separator"></div>

          <div class="content">
            <slot></slot>
          </div>
        </div>
      </Host>
    );
  }
}
