 */

@import 'common-variables';
@import 'mixins/text-truncation';
@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;

  .message-container {
    display: flex;
    flex-direction: row;
    align-items: flex-start;
    flex-wrap: nowrap;
    justify-content: space-between;
    min-height: 3.375rem;
    margin: $small-space $small-space 0rem $small-space;
    padding: calc(#{$medium-space} - var(--theme-message-bar--border-thickness))
      $medium-space
      calc(#{$medium-space} - var(--theme-message-bar--border-thickness))
      $default-space;
    border-radius: var(--theme-message-bar--border-radius);
    background-color: var(--theme-messagebar--background);
  }

  .danger {
    border: solid var(--theme-message-bar--border-thickness)
      var(--theme-color-alarm);
  }

  .warning {
    border: solid var(--theme-message-bar--border-thickness)
      var(--theme-color-warning);
  }

  .info {
    border: solid var(--theme-message-bar--border-thickness)
      var(--theme-color-info);
  }

  .message-content {
    @include ellipsis;
    flex-grow: 1;
    align-self: center;
    min-height: 1.25rem;
    padding: 0 $default-space;
    font-weight: bold;
    white-space: normal;
  }

  ix-icon {
    margin-top: $tiny-space;
  }
}
/*
 * SPDX-FileCopyrightText: 2023 Siemens AG
 *
 * SPDX-License-Identifier: MIT
 *
 * This source code is licensed under the MIT license found in the
 * LICENSE file in the root directory of this source tree.
 */

import {
  Component,
  Event,
  EventEmitter,
  h,
  Host,
  Prop,
  State,
} from '@stencil/core';
import anime from 'animejs';
import { NotificationColor } from '../utils/notification-color';

@Component({
  tag: 'ix-message-bar',
  styleUrl: 'message-bar.scss',
  shadow: true,
})
export class MessageBar {
  /**
   * Specifies the type of the alert.
   */
  @Prop() type: 'danger' | 'warning' | 'info' = 'info';

  /**
   * If true, close button is enabled and alert can be dismissed by the user
   */
  @Prop() dismissible = true;

  /**
   * An event emitted when the close button is clicked
   */
  @Event() closedChange: EventEmitter;

  @State() icon: 'error' | 'warning' | 'info';

  @State() color: NotificationColor;

  private static readonly duration = 300;

  private divElement?: HTMLElement;

  componentWillRender() {
    if (this.type === 'danger') {
      this.icon = 'error';
      this.color = 'color-alarm';
    }

    if (this.type === 'info') {
      this.icon = 'info';
      this.color = 'color-info';
    }

    if (this.type === 'warning') {
      this.icon = 'warning';
      this.color = 'color-warning';
    }
  }

  private closeAlert(el: HTMLElement) {
    anime({
      targets: el,
      duration: MessageBar.duration,
      opacity: [1, 0],
      easing: 'easeOutSine',
      complete: () => {
        el.classList.add('d-none');
      },
    });
    this.closedChange.emit();
  }

  render() {
    return (
      <Host>
        <div
          class={{ 'message-container': true, [this.type]: true }}
          role="alert"
          ref={(el) => (this.divElement = el as HTMLElement)}
        >
          <ix-icon color={this.color} name={this.icon}></ix-icon>
          <div class="message-content">
            <slot></slot>
          </div>
          {this.dismissible ? (
            <ix-icon-button
              icon={'close'}
              size="24"
              ghost={true}
              onClick={() => {
                this.closeAlert(this.divElement);
              }}
              data-testid="close-btn"
            ></ix-icon-button>
          ) : (
            ''
          )}
        </div>
      </Host>
    );
  }
}
