:host {
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  padding: 0px;

  .titleGroup {
    display: flex;
    flex-direction: column;
    flex: 1 1 0%;
  }

  .subtitle {
    margin-top: 0.5rem;
  }

  .backButton {
    margin-right: 0.5rem;
  }

  .buttons {
    flex: 0 0 auto;
  }
}
