@import 'common-variables';
@import 'legacy/fonts';
@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;

  display: block;
  background-color: var(--theme-nav-overlay--background);
  padding: $medium-space $default-space $default-space $x-large-space;
  flex-grow: 1;
  position: absolute;
  width: 100%;
  height: 100%;

  .settings-header {
    display: flex;
    justify-content: space-between;
    flex-direction: row;
    align-items: center;
    height: $x-large-space;
    margin-bottom: $small-space;

    h2 {
      color: var(--theme-nav-overlay-header--color);
      margin-bottom: 1rem;
    }
  }

  ix-tabs {
    margin-bottom: 1.5rem;
  }

  .settings-tabs {
    margin-bottom: $large-space;
  }
}
