:host(.keyValueList) {
  ::slotted(ix-key-value) {
    border-bottom: 1px solid var(--theme-color-weak-bdr);
  }
}

:host(.keyValueList.keyValueList--striped) {
  ::slotted(ix-key-value:nth-child(odd)) {
    background: var(--theme-color-ghost-alt);
  }
}
