@import 'common-variables';

:host {
  display: flex;
  position: relative;
  flex-direction: row;
  align-items: center;
  margin-top: 0.25rem;
  margin-bottom: $small-space;
  gap: $default-space;

  width: 100%;

  .title-actions {
    display: flex;
    position: relative;
    flex-direction: row;
    gap: 0.25rem;
    margin-left: auto;
    margin-right: 0px;
  }
}
import { Component, h, Host } from '@stencil/core';

/**
 * @slot title-actions - Place additional actions inside title
 *
 * @since 1.6.0
 */
@Component({
  tag: 'ix-card-title',
  styleUrl: 'card-title.scss',
  shadow: true,
})
export class CardHeader {
  render() {
    return (
      <Host>
        <slot></slot>
        <div class="title-actions">
          <slot name="title-actions"></slot>
        </div>
      </Host>
    );
  }
}
