@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;

  display: flex;
  align-items: center;
  position: relative;
  height: 2.5rem;
  width: auto;

  padding: 0.25rem 1rem;

  overflow: hidden;
  cursor: default;

  color: var(--theme-color-soft-text);
}
