@import 'common-variables';
@import 'mixins/text-truncation';
@import 'mixins/hover';
@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;
  display: block;
  position: relative;
  margin-bottom: 0.5rem;
  margin-right: 0.75rem;

  .avatar {
    all: unset;
    box-sizing: border-box;

    display: flex;
    align-items: center;
    height: 2.5rem;
    width: 100%;
    max-height: 2.5rem;
    padding-left: 0.25rem;
    margin-left: 0.41rem;
    margin-right: 0.35rem;
    transition: 0.15s;

    > .avatar-image {
      height: 2rem;
      width: 2rem;
      min-height: 2rem;
      min-width: 2rem;
      border-radius: 100px;
    }

    > .avatar-initials {
      display: flex;
      align-items: center;
      justify-content: center;
      height: 2rem;
      width: 2rem;
      min-height: 2rem;
      min-width: 2rem;
      border-radius: 100px;
      background-color: var(--theme-color-component-3);
    }

    #avatar-path-background {
      fill: var(--theme-avatar--background);
    }

    #avatar-path-person {
      fill: var(--theme-color-4);
    }

    .avatar-name {
      display: flex;
      flex-direction: column;
      overflow: hidden;
      white-space: nowrap;
      margin-left: $default-space;
      line-height: 1.14;

      .text-default-single {
        @include ellipsis;
      }
    }

    border-radius: 1.25rem;

    @include hover {
      background-color: var(--theme-avatar-btn--background--hover);
      color: var(--theme-avatar-btn--color--hover);
    }

    @include active {
      background-color: var(--theme-avatar-btn--background--active);
      color: var(--theme-avatar-btn--color--active);
    }
  }

  .avatar:focus-visible {
    outline: 1px solid var(--theme-color-focus-bdr);
  }
}
