import { Component, h, Host } from '@stencil/core';

/**
 * @since 2.0.0
 */
@Component({
  tag: 'ix-row',
  styleUrl: 'row.scss',
  shadow: true,
})
export class Row {
  render() {
    return (
      <Host>
        <slot></slot>
      </Host>
    );
  }
}
