 */
@import 'mixins/hover';
@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;
  display: flex;
  position: relative;
  flex-direction: column;
  width: 100%;
  transition: var(--theme-default-time) ease-in-out;

  .expand-action {
    all: unset;
    display: flex;
    position: relative;

    appearance: button;

    height: 2.5rem;
    width: 100%;

    background-color: var(--theme-color-component-1);
    align-items: center;
    justify-content: flex-start;
    @include ghost-hover-pressed();

    @include focus-visible {
      outline: 1px solid var(--theme-color-focus-bdr);
      outline-offset: var(--theme-btn--focus--outline-offset);
    }
  }

  .expand-action::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background-color: var(--ix-card-background);
    z-index: -1;
  }

  .expand-action:not(.show),
  .expand-action:not(.show)::before {
    border-bottom-left-radius: var(--theme-default-border-radius);
    border-bottom-right-radius: var(--theme-default-border-radius);
  }

  .expand-icon {
    margin-left: 0.5rem;
    transition: var(--theme-default-time) transform ease-in-out;
  }

  .expand-icon.show {
    transform: rotate(90deg);
  }

  .expand-content {
    display: grid;
    position: relative;
    grid-template-rows: 0fr;
    transition: var(--theme-default-time) ease-in-out;
    width: 100%;
    max-height: 14.75rem;
    opacity: 0;
    background-color: var(--theme-color-component-1);
  }

  .expand-content.show {
    grid-template-rows: 14.75rem;
    opacity: 1;
  }

  .expand-content-inner {
    position: relative;
    height: 100%;
    overflow: hidden;
  }

  .expand-content.show .expand-content-body {
    display: none;
    overflow: hidden;
    color: var(--theme-color-std-text);
  }

  .expand-content.show .expand-content-body {
    display: block;
    overflow: auto;
    height: calc(100% - 1rem);
  }

  .expand-content-footer {
    display: block;
    position: relative;
    width: 100%;
    height: 1rem;

    margin-top: auto;
    margin-bottom: 0px;

    background-color: var(--theme-color-component-1);
    border-bottom-left-radius: var(--theme-default-border-radius);
    border-bottom-right-radius: var(--theme-default-border-radius);
  }
}
import {
  Component,
  Element,
  Event,
  EventEmitter,
  h,
  Host,
  State,
} from '@stencil/core';

let accordionControlId = 0;
const getAriaControlsId = (prefix: string = 'expand-content') => {
  return [prefix, accordionControlId++].join('-');
};

export type CardAccordionExpandChangeEvent = {
  expand: boolean;
  nativeEvent: Event;
};

/**
 * @since 1.6.0
 */
@Component({
  tag: 'ix-card-accordion',
  styleUrl: 'card-accordion.scss',
  shadow: true,
})
export class CardAccordion {
  @Element() hostElement: HTMLIxCardAccordionElement;

  /**
   * @internal
   */
  @Event() accordionExpand: EventEmitter<CardAccordionExpandChangeEvent>;

  @State() expandContent = false;

  get expandedContent() {
    return this.hostElement.shadowRoot.querySelector('.expand-content');
  }

  onExpandActionClick(event: Event) {
    event.preventDefault();
    event.stopPropagation();
    this.expandContent = !this.expandContent;
    this.accordionExpand.emit({
      expand: this.expandContent,
      nativeEvent: event,
    });

    if (this.expandContent) {
      this.scrollExpandedContentIntoView();
    }
  }

  private scrollExpandedContentIntoView() {
    setTimeout(() => {
      const rect = this.expandedContent.getBoundingClientRect();
      if (rect.bottom > window.innerHeight) {
        this.hostElement.shadowRoot
          .querySelector('.expand-content')
          .scrollIntoView(false);
      }
    }, 150);
  }

  render() {
    return (
      <Host slot="card-accordion">
        <button
          tabIndex={0}
          class={{ 'expand-action': true, show: this.expandContent }}
          onClick={(event) => this.onExpandActionClick(event)}
          role="button"
          type="button"
          aria-expanded={this.expandContent}
          aria-controls={getAriaControlsId()}
        >
          <ix-icon
            name={'chevron-right-small'}
            class={{
              'expand-icon': true,
              show: this.expandContent,
            }}
          ></ix-icon>
        </button>
        <div
          class={{
            'expand-content': true,
            show: this.expandContent,
          }}
        >
          <div class="expand-content-inner">
            <div class="expand-content-body">
              <slot></slot>
            </div>
            <div class="expand-content-footer"></div>
          </div>
        </div>
      </Host>
    );
  }
}
