import {
  Component,
  Element,
  Event,
  EventEmitter,
  Fragment,
  h,
  Host,
  Prop,
  State,
} from '@stencil/core';
import animejs from 'animejs';
import { BaseButton, BaseButtonProps } from '../button/base-button';
import { a11yHostAttributes } from '../utils/a11y';

export type BreadcrumbItemLinkTarget =
  | '_self'
  | '_blank'
  | '_parent'
  | '_top'
  | string;

@Component({
  tag: 'ix-breadcrumb-item',
  styleUrl: 'breadcrumb-item.scss',
  shadow: true,
})
export class BreadcrumbItem {
  @Element() hostElement!: HTMLIxBreadcrumbItemElement;

  /**
   * Breadcrumb label
   */
  @Prop() label: string;

  /**
   * Icon to be displayed next ot the label
   */
  @Prop() icon: string;

  /**@internal */
  @Prop() ghost: boolean = true;

  /**@internal */
  @Prop() visible = true;

  /**@internal */
  @Prop() showChevron = true;

  /** @internal */
  @Prop() isDropdownTrigger = false;

  /**@internal */
  @Event() itemClick: EventEmitter<string>;

  @State() a11y: any;

  componentDidLoad() {
    this.animationFadeIn();
  }

  componentWillLoad() {
    this.a11y = a11yHostAttributes(this.hostElement, [
      'aria-describedby',
      'aria-controls',
      'aria-expanded',
    ]);
  }

  animationFadeIn() {
    animejs({
      targets: this.hostElement,
      duration: 150,
      opacity: [0, 1],
      translateX: ['-100%', '0%'],
      easing: 'linear',
    });
  }

  render() {
    const props: BaseButtonProps = {
      variant: this.ghost ? 'primary' : 'secondary',
      outline: false,
      ghost: this.ghost,
      iconOnly: false,
      iconOval: false,
      disabled: false,
      icon: this.icon,
      iconSize: '16',
      loading: false,
      selected: false,
      type: 'button',
      tabIndex:
        this.hostElement.tabIndex !== -1 ? this.hostElement.tabIndex : 0,
      extraClasses: {
        'dropdown-trigger': this.isDropdownTrigger,
      },
      ariaAttributes: this.a11y,
    };

    if (!this.visible) {
      return <Host class={'invisible'}></Host>;
    }

    return (
      <Host
        class={{
          'hide-chevron': !this.showChevron,
        }}
        onClick={() => this.itemClick.emit(this.label)}
      >
        <li>
          <BaseButton
            {...props}
            afterContent={
              <Fragment>
                {this.showChevron ? (
                  <ix-icon
                    name={'chevron-right-small'}
                    size="16"
                    class={'chevron'}
                  ></ix-icon>
                ) : null}
              </Fragment>
            }
          >
            {this.label}
            <slot></slot>
          </BaseButton>
        </li>
      </Host>
    );
  }
}
