 *
 * This source code is licensed under the MIT license found in the
 * LICENSE file in the root directory of this source tree.
 */

@import 'common-variables';
@import 'legacy/mixins/fonts';
@import 'mixins/hover';
@import 'mixins/text-truncation';
@import 'mixins/shadow-dom/component';

:host {
  @include ix-component;

  display: flex;
  flex-grow: 1;
  height: $large-control-height;
  border-radius: var(--theme-kpi--border-radius);
  padding: 0.375rem $tiny-space;
  min-width: 0;

  span {
    @include ellipsis;
  }

  .kpi-container {
    display: flex;
    height: 100%;
    width: 100%;
    border-block-end: 2px solid grey;

    &.alarm {
      border-block-end-color: var(--theme-color-alarm);
    }

    &.warning {
      border-block-end-color: var(--theme-color-warning);
    }

    .kpi-label {
      display: flex;
      align-items: center;
      color: var(--theme-kpi-display-label--color);
      flex-grow: 1;
      flex-shrink: 9999;

      ix-icon {
        margin-inline-end: $tiny-space;
      }
    }

    .kpi-value-container {
      display: flex;
      align-items: flex-end;
    }

    .kpi-value {
      @include text-xl;

      color: var(--theme-kpi-display-value--color);
    }

    .kpi-unit {
      margin-inline-start: $small-space;
      color: var(--theme-kpi-display-units--color);
    }

    .kpi-label,
    .kpi-unit {
      margin-block-start: 0.125rem;
    }
  }

  @include hover {
    background-color: var(--theme-kpi-display--background--hover);
  }

  @include active {
    background-color: var(--theme-kpi-display--background--active);
  }

  @include focus-visible {
    outline: 1px solid var(--focus--border-color);
  }
}

:host(.stacked) {
  height: 3.75rem;

  .kpi-container {
    justify-content: center;
    flex-wrap: wrap;

    .kpi-label {
      width: 100%;
      justify-content: center;
    }
  }
}
/*
 * SPDX-FileCopyrightText: 2023 Siemens AG
 *
 * SPDX-License-Identifier: MIT
 *
 * This source code is licensed under the MIT license found in the
 * LICENSE file in the root directory of this source tree.
 */

import { Component, h, Host, Prop } from '@stencil/core';

@Component({
  tag: 'ix-kpi',
  styleUrl: 'kpi.scss',
  shadow: true,
})
export class Kpi {
  /**
   *
   */
  @Prop() label: string;

  /**
   *
   */
  @Prop() value: string | number;

  /**
   *
   */
  @Prop() unit: string;

  /**
   *
   */
  @Prop() state: 'neutral' | 'warning' | 'alarm' = 'neutral';

  /**
   *
   */
  @Prop() orientation: 'horizontal' | 'vertical' = 'horizontal';

  private getStateIcon() {
    switch (this.state) {
      case 'alarm':
        return (
          <ix-icon
            color="kpi-display-icon--color"
            name={'alarm'}
            size="16"
          ></ix-icon>
        );

      case 'warning':
        return (
          <ix-icon
            color="kpi-display-icon--color"
            name={'warning'}
            size="16"
          ></ix-icon>
        );

      default:
        return '';
    }
  }

  private getTooltipText() {
    let tooltip = `${this.label}: ${this.value}`;

    if (this.unit) {
      tooltip = tooltip.concat(` ${this.unit}`);
    }

    return tooltip;
  }

  render() {
    return (
      <Host
        title={this.getTooltipText()}
        tabindex="1"
        class={{
          stacked: this.orientation === 'vertical',
        }}
      >
        <div
          class={{
            'kpi-container': true,
            alarm: this.state === 'alarm',
            warning: this.state === 'warning',
          }}
        >
          <span class="kpi-label">
            {this.getStateIcon()}
            <span class="kpi-label-text">{this.label}</span>
          </span>
          <span class="kpi-value-container">
            <span class="kpi-value">{this.value}</span>
            {this.unit ? <span class="kpi-unit">{this.unit}</span> : ''}
          </span>
        </div>
      </Host>
    );
  }
}
